#creation du dossier profilnew-Item \\B64HV1\C$\_Profil -itemType DirectoryNew-SmbShare -Name Profil$ -Path C:\_Profil -CimSession B64HV1 ` -FullAccess "Tout le monde" -CachingMode None -FolderEnumerationMode AccessBasedicacls.exe \\B64HV1\C$\_Profil /inheritance:ricacls.exe \\B64HV1\C$\_Profil /grant "Administrateurs:(OI)(CI)(F)" ` "SYSTEM:(OI)(CI)(F)" "TECH:(OI)(CI)(F)" "DROITS DU PROPRIÉTAIRE:(OI)(CI)(M)" ` "Utilisateurs du domaine:(RX)"###########################Aller chercher les users#remove-item -path \\B64HV1\PERSO$\"B64*" -force -recurse$users = Get-AdUser -Filter 'Name -like "B64*"'foreach ($user in $users){    $nom = $user.name;    new-Item \\B64HV1\PERSO$\$nom -itemType Directory    icacls.exe \\B64HV1\PERSO$\$nom /grant $nom":(OI)(CI)(M)"    new-Item \\B64HV1\PROFIL$\$nom.V4 -itemType Directory    icacls.exe \\B64HV1\PROFIL$\$nom.V4 /grant $nom":(OI)(CI)(M)"    Set-ADUser -Identity $nom -HomeDirectory \\B64HV1\PERSO$\$nom -HomeDrive J: -ProfilePath \\B64HV1\PROFIL$\$nom}